let activeLink = null;
window.addEventListener("scroll", () => {
  $$(".section").forEach(section => {
    const {
      y
    } = section.getBoundingClientRect();
    if (y <= 0) {
      if (activeLink != null) {
        activeLink.classList.remove("active");
      }
      activeLink = $(`.nav-link[href~="#${section.id}"]`);
      activeLink.classList.add("active");
    }
  });
});
